import "./style.css";
import { FaTrash } from "react-icons/fa";

const Card = ({ transaction, listTransactions, setListTransactions }) => {
  const handleDelet = (item) => {
    const filtered = listTransactions.filter((itens) => {
      return itens !== item;
    });

    setListTransactions(filtered);
  };
  return (
    <div
      className={
        transaction.type === "entrada" ? "Div-card-entrada" : "Div-card-saida"
      }
    >
      <div className="Div-transaction">
        <h2>{transaction.description}</h2>
        <p>{transaction.type}</p>
      </div>
      <div className="Div-valueDelete">
        <p>R$ {transaction.value}</p>
        <button onClick={() => handleDelet(transaction)}>
          <FaTrash />
        </button>
      </div>
    </div>
  );
};
export default Card;
