import Card from "../Card";
import "./style.css";
import svg from "./../../img/nenhum.svg";

const List = ({ listTransactions, setListTransactions }) => {
  const filtered = "";
  console.log(listTransactions);
  const filterAll = () => {
    const newArray = listTransactions.filter((item) => {
      return item;
    });
    filtered = newArray;
  };
  console.log(newArray);

  return (
    <div className="Div-list">
      <div className="Div-listNav">
        <div className="Div-listP">
          <p>Resumo financeiro</p>
        </div>
        <div className="Div-listButtons">
          <button className="List-buttonOne" onClick={() => filterAll()}>
            Todos
          </button>
          <button className="List-buttonTwo">Entradas</button>
          <button className="List-buttonThree">Despesas</button>
        </div>
      </div>
      <div className="Div-listCard">
        {listTransactions.length > 0 ? (
          listTransactions.map((transaction, index) => (
            <Card
              listTransactions={listTransactions}
              transaction={transaction}
              key={index}
              setListTransactions={setListTransactions}
            />
          ))
        ) : (
          <img src={svg} alt=""></img>
        )}
      </div>
    </div>
  );
};
export default List;
